<?php require_once __DIR__.'/../includes/db_connect.php'; if($_SERVER['REQUEST_METHOD']!=='POST') { echo 'Method not allowed'; exit;} $name=$_POST['fullname']??''; $email=$_POST['email']??''; $pass=$_POST['password']??''; if($pdo){ $stmt=$pdo->prepare('INSERT INTO users (fullname,email,password,created_at) VALUES (?,?,?,NOW())'); $stmt->execute([$name,$email,password_hash($pass,PASSWORD_DEFAULT)]); echo 'OK'; } else { $f=__DIR__.'/../data_users.json'; $users=file_exists($f)?json_decode(file_get_contents($f),true):[]; $users[]=['fullname'=>$name,'email'=>$email,'password'=>password_hash($pass,PASSWORD_DEFAULT),'created_at'=>date('c')]; file_put_contents($f,json_encode($users,JSON_PRETTY_PRINT)); echo 'OK (json)'; } ?>